import React, { useState } from "react";

// const initialState = {
//     result: 0
// };

// function reducer(state, action) {
//     switch (action.type) {
//         case "add":
//             return { result: action.payload }
//         case "subtract":
//             return;
//         case "divide":
//             return;
//         case "multiply":
//             return;
//         default:
//             return;
//     }
// }


function Calculator() {

    const [firstNum, setFirstNum] = useState(0);
    const [secNum, setSecNum] = useState(0);
    const [result, setResult] = useState(0);

    const handleChange = e => {
        setFirstNum(e.target.value);
        setSecNum(e.target.value)
    }
    const handleAdd = (e) => {
        setResult(firstNum + secNum);
    }

    return (
        <React.Fragment>
            <div className="container" style={{ display: "flex", flexDirection: "column", gap: "12px" }}>
                <input type="text" name="fnum" value={firstNum} onChange={handleChange} />
                <input type="text" name="snum" value={secNum} onChange={handleChange} />
                <button onClick={handleAdd}>Add</button>
                <hr />
                <span>{result}</span>
            </div>
        </React.Fragment>
    )
}

export default Calculator;
