import "./Currency.css";

function Main() {
  return (
    <div className="container">
      <div className="main">
        <Input />
        <CurrencyType>
          <select>
            <option>NGN</option>
            <option>USD</option>
            <option>EUR</option>
          </select>
        </CurrencyType>
        <CurrencyType>
          <select>
            <option>NGN</option>
            <option>USD</option>
            <option>EUR</option>
          </select>
        </CurrencyType>
      </div>

      <Output />
    </div>
  );
}

function Input() {
  return (
    <>
      <input type="text" name="amount" placeholder="amount" />
    </>
  );
}

function CurrencyType({ children }) {
  return <>{children}</>;
}

function Output() {
  return <p>OUTPUT</p>;
}

export default Main;
