import { useState } from "react";
import "./eat.css";

const initialFriends = [
  {
    id: 118836,
    name: "Clark",
    image: "https://i.pravatar.cc/48?u=118836",
    balance: -7,
  },
  {
    id: 933372,
    name: "Sarah",
    image: "https://i.pravatar.cc/48?u=933372",
    balance: 20,
  },
  {
    id: 499476,
    name: "Anthony",
    image: "https://i.pravatar.cc/48?u=499476",
    balance: 0,
  },
];

function Button({ children, click }) {
  return (
    <button className="btn" onClick={click}>
      {children}
    </button>
  );
}

export default function Main() {
  const [showAddFriend, setShowAddFriend] = useState(false);
  const [showFriend, setShowFriend] = useState(null);
  const [name, setName] = useState("");
  const [image, setImage] = useState("https://i.pravatar.cc/48");
  const [friends, setFriends] = useState(initialFriends);
  const [billExpense, setUserExpense] = useState("");
  const [userExpense, setFriendExpense] = useState("");

  function handleShowAddFriend() {
    setShowAddFriend((show) => !show);
  }

  function handleSetName(e) {
    setName(e.target.value);
  }

  function handleSetImage(e) {
    setImage(e.target.value);
  }

  function handleAddFriend(e) {
    e.preventDefault();
    if (!name || !image) return;

    const id = new Date();

    const newFriend = {
      name,
      image: image + `/+${id}`,
      balance: 0,
      id: id,
    };

    setFriends((prevFriends) => [...prevFriends, newFriend]);
    setImage("https://i.pravatar.cc/48");
    setName("");
    setShowAddFriend(false);
  }

  function handleFriendBill(id) {
    const searchFriend = friends.filter((friend) => friend.id === id);
    // console.log(searchFriend[0]);
    setShowFriend(searchFriend[0]);
  }

  return (
    <div className="container">
      <div className="sidebar">
        <FriendsList friends={friends} handleFriendBill={handleFriendBill} />
        {showAddFriend && (
          <FormAddFriends
            name={name}
            image={image}
            handleSetName={handleSetName}
            handleSetImage={handleSetImage}
            handleAddFriend={handleAddFriend}
          />
        )}
        <div className="btn-add">
          <Button click={handleShowAddFriend}>
            {showAddFriend ? "Close" : "Add friend"}
          </Button>
        </div>
      </div>
      {showFriend && (
        <BillForm
          friend={showFriend}
          billExpense={billExpense}
          friendExpense={userExpense}
        />
      )}
    </div>
  );
}

function FriendsList({ friends, handleFriendBill }) {
  return (
    <ul>
      {friends.map((friend) => (
        <Friends
          key={friend.id}
          friend={friend}
          handleFriendBill={handleFriendBill}
        />
      ))}
    </ul>
  );
}

function Friends({ friend, handleFriendBill }) {
  return (
    <li className="friendList">
      <img src={friend.image} alt={friend.name} />
      <p>
        <h4>{friend.name}</h4>
        <p> You owe Clark $7</p>
      </p>
      <Button click={() => handleFriendBill(friend.id)}>Select</Button>
    </li>
  );
}

function FormAddFriends({
  name,
  handleSetName,
  image,
  handleSetImage,
  handleAddFriend,
}) {
  return (
    <form className="addForm" onSubmit={handleAddFriend}>
      <div className="fields">
        <label>🧑‍🤝‍🧑Friend name</label>
        <input type="text" value={name} onChange={handleSetName} />
      </div>

      <div className="fields">
        <label>🖼️Image name</label>
        <input type="text" value={image} onChange={handleSetImage} />
      </div>

      <Button>Add</Button>
    </form>
  );
}

function BillForm({ friend, billExpense, userExpense }) {
  return (
    <div className="bill-form">
      <form>
        <h2 className="form-title">split a Bill with {friend.name}</h2>
        <div className="fields">
          <label>💷 Bill value</label>
          <input type="text" value={Number(billExpense)} />
        </div>

        <div className="fields">
          <label>🧍Your expense</label>
          <input type="text" value={Number(userExpense)} />
        </div>
        <div className="fields">
          <label>🧑‍🤝‍🧑{friend.name}'s expense</label>
          <input type="text" disabled />
        </div>
        <div className="fields">
          <label>😀 Who is paying the bill</label>
          <select>
            <option>User</option>
            <option>{friend.name}</option>
          </select>
        </div>
      </form>

      <div className="btn-split">
        <Button>Split Bill</Button>
      </div>
    </div>
  );
}
