import React, { useReducer } from "react";

import Button from "./Button";
import "./Bank.css";

const intialState = {
  balance: 0,
  loan: 0,
  isActive: false,
};

function reducer(state, action) {
  switch (action.type) {
    case "open":
      return {
        ...state,
        balance: state.balance + action.payload,
        isActive: true,
      };
    case "loan":
      return { ...state, loan: state.loan + action.payload };
    case "deposit":
      return { ...state, balance: state.balance + action.payload };
    default:
      throw new Error("Unknown Action");
  }
}

function Main() {
  const [state, dispatch] = useReducer(reducer, intialState);

  const disabled = state.isActive;
  return (
    <div className="main">
      <p>Balance: {state.balance}</p>
      <p>Loan: X</p>
      <Button
        click={() => dispatch({ type: "open", payload: 500 })}
        disabled={state.isActive}
      >
        Open Account
      </Button>
      <Button>Deposit 150</Button>
      <Button>Withdraw 50</Button>
      <Button>Request loan of 5000</Button>
      <Button>Pay load</Button>
      <Button>Close Account</Button>
    </div>
  );
}

export default Main;
