import React, { useState } from "react";
import { useFetch } from "./useFetch";
import "./Weather.css";

function Weather() {
  const [city, setCity] = useState("Abuja");
  const [country, setCountry] = useState("");
  const [code, setCode] = useState("");

  const handleCity = (e) => {
    setCity(e.target.value);
  };

  return (
    <div className="container">
      <div className="content">
        <h2>Classy Weather</h2>
        <Input city={city} handleCity={handleCity} />
        <h2>Weather {country ? `${country} ${code}` : ""}</h2>
        <WeatherDetails city={city} setCountry={setCountry} setCode={setCode} />
      </div>
    </div>
  );
}

function Input({ city, handleCity }) {
  return (
    <div className="city-input">
      <input
        type="text"
        placeholder="City Name"
        value={city}
        onChange={handleCity}
      />
    </div>
  );
}

// function Loader() {
//   return (
//     <>
//       <p className="loader">Loading...</p>
//     </>
//   );
// }

function WeatherDetails({ city, setCountry, setCode }) {
  // const [isToday, setIsToday] = useState(false);
  const [data, countryName, code] = useFetch(city);
  setCountry(countryName);
  setCode(code);
  // const { dates, w_code, maxTemp, minTemp } = data;
  // console.log(data);
  return (
    <ul className="weather-list">
      {data.time.map((t, i) => (
        <Detail
          key={i}
          max={data.temperature_2m_max.at(i)}
          min={data.temperature_2m_min.at(i)}
          date={t}
          isToday={i === 0}
        />
      ))}
    </ul>
  );
}

function Detail({ min, max, date, isToday }) {
  return (
    <li className="weather-item">
      <span>{min > 20 ? "‚òÅÔ∏è" : "üå•Ô∏è"}</span>
      <p>{isToday ? "Today" : formatDay(date)}</p>
      <p>
        {`${Math.ceil(min)}`}&#176; - {`${Math.ceil(max)}`}&#176;
      </p>
    </li>
  );
}

export default Weather;

function formatDay(dateStr) {
  return new Intl.DateTimeFormat("en", {
    weekday: "short",
  }).format(new Date(dateStr));
}
