import { useState } from "react";
// import {addDays } from "date-fns";
import "./DaysDate.css";


function Counter() {

    const [count, setCount] = useState(0);
    // const [step, setStep] = useState(1)
    const [range, setRange] = useState(1);

    const handleIncrease = () => {
        setCount(count + range);
    }
    const handleDecrease = () => {
        setCount(prevCount => prevCount - range);
    }

    // const handleStepIncrease = () => setStep((step) => step + 1);

    // const handleStepDecrease = () => setStep((step) => step - 1);

    const handleChange = () => setRange(range => range + 1)

    const date = new Date('june 27 2027');
    date.setDate(date.getDate() + count);

    return (
        <>
            <div className="container">
                <div className="step">
                    <input type="range" min="1" max="10" value={range} onChange={handleChange} />
                    {/* <button onClick={handleStepDecrease}>-</button>
                    <p>Step: {step}</p>
                    <button onClick={handleStepIncrease}>+</button> */}
                </div>
                <div className="counter">
                    <button onClick={handleDecrease}>-</button>
                    <input type="text" name="count" id="count" value={count} onChange={(e) => setCount(e.target.value)} />
                    <button onClick={handleIncrease}>+</button>

                </div>
                <p>{count === 0 ? "Today is " : count > 0 ? `${count} Days from today is ` : `${Math.abs(count)} Days ago was`} {date.toDateString()}</p>
            </div>
        </>
    )
}

export default Counter;
