import React, { useEffect, useState } from "react";
import "./popcorn.css";

const KEY = "6b44cc4";

function MainApp() {
  const [query, setQuery] = useState("Fire proof");

  const handleChangeQuery = (e) => {
    setQuery(e.target.value);
  };

  return (
    <div className="container">
      <NavBar>
        <Logo />
        <SearchBar query={query} handleChangeQuery={handleChangeQuery} />
        <Notification />
      </NavBar>
      <Main query={query} />
    </div>
  );
}

function NavBar({ children }) {
  return <div className="navbar">{children}</div>;
}

function Logo() {
  return (
    <div className="logo">
      <p>
        <span>🍿</span>usePopcorn
      </p>
    </div>
  );
}

function SearchBar({ query, handleChangeQuery }) {
  return (
    <div className="search">
      <input
        type="text"
        placeholder="Search movies"
        value={query}
        onChange={handleChangeQuery}
      />
    </div>
  );
}

function Notification() {
  return (
    <div className="notification">
      <span>Found X Result</span>
    </div>
  );
}

function Main({ query }) {
  const [movies, setMovies] = useState([]);
  const [movieId, setMovieId] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [hasError, setHasError] = useState("");
  const [watchedList, setWatchedList] = useState([]);
  const [showWatched, setShowWatched] = useState(false);

  const handleSelectedMovie = (id) => {
    setMovieId(id);
  };
  const err = "⛔ Error fetching Movie";

  const handleAddMovie = (id) => {
    const movie = movies.filter((m) => m.imdbID === id).at(0);
    // console.log(movie.at(0));
    setWatchedList((prevMovies) => [...prevMovies, movie]);
    setShowWatched(true);
  };

  useEffect(
    function () {
      setIsLoading(true);

      async function getMovies() {
        try {
          const res = await fetch(
            `http://www.omdbapi.com/?apikey=${KEY}&s=${query}`
          );
          if (!res.ok) throw new Error("Something went wrong fetching movies");

          const data = await res.json();
          if (data.Response === "False") throw new Error("Movie not found");

          setMovies(data.Search);
          // console.log(data.Search);
          setHasError("");
        } catch (error) {
          if (err.name !== "AbortError") {
            setHasError(error.message);
          }
        } finally {
          setIsLoading(false);
        }
      }

      if (query.length < 3) {
        setMovies([]);
        setHasError("");
        return;
      }

      getMovies();
    },
    [query]
  );

  return (
    <div className="content">
      <Box className="box">
        {isLoading && <Loader />}

        {!isLoading && !hasError && (
          <MoviesList
            movies={movies}
            handleSelectedMovie={handleSelectedMovie}
          />
        )}

        {!hasError && <Error message={hasError} />}
      </Box>
      <Box className="box">
        {movieId === null ? (
          <Summary />
        ) : showWatched ? (
          <>
            {" "}
            <Summary /> <WatchedListMovies watchedList={watchedList} />{" "}
          </>
        ) : (
          <MovieDetails movieId={movieId} handleAddMovie={handleAddMovie} />
        )}
      </Box>
    </div>
  );
}

function Box({ children, className }) {
  return <div className={className}>{children}</div>;
}

function Summary() {
  return (
    <header className="box-header">
      <h4>Movies You Watched</h4>
      <div className="movies-rec">
        <div className="minutes">
          <div>
            <span>#️⃣</span>
            <span>X Movies</span>
          </div>
        </div>
        <div>
          <span>⭐ </span>
          <span>x.x</span>
        </div>
        <div>
          <span>🌟 </span>
          <span>x.x</span>
        </div>
        <div>
          <span>⏲️ </span>
          <span>x.x min</span>
        </div>
      </div>
    </header>
  );
}

function Loader() {
  return (
    <div className="loader">
      <span>Loading . . .</span>
    </div>
  );
}

function Error({ message }) {
  return (
    <div className="error">
      <span>{message}</span>
    </div>
  );
}

function MoviesList({ movies, handleSelectedMovie }) {
  return (
    <ul>
      {movies.map((movie, key) => {
        return (
          <Movie
            key={movie.imdbID}
            movie={movie}
            handleSelectedMovie={handleSelectedMovie}
          />
        );
      })}
    </ul>
  );
}

function Movie({ movie, handleSelectedMovie }) {
  return (
    <li className="movie" onClick={() => handleSelectedMovie(movie.imdbID)}>
      <img src={movie.Poster} alt={movie.Title} />
      <div className="movieDetails">
        <p>{movie.Title}</p>
        <span>🗓️ {movie.Year}</span>
      </div>
    </li>
  );
}

function Button({ children, click, className }) {
  return (
    <>
      <button onClick={click} className={className}>
        {children}
      </button>
    </>
  );
}

function MovieDetails({ movieId, handleAddMovie }) {
  const [movie, setMovie] = useState([]);

  useEffect(
    function () {
      async function getSelectedMovie() {
        const res = await fetch(
          `http://www.omdbapi.com/?apikey=${KEY}&i=${movieId}`
        );
        const data = await res.json();
        // console.log(data);
        setMovie(data);
      }

      getSelectedMovie();
    },
    [movieId]
  );

  return (
    <div className="movie-details-container">
      <div className="movie-details-header">
        <img src={movie.Poster} alt={movie.Title} />
        <div className="movie-details-title">
          <h3>{movie.Title}</h3>
          <p>
            {movie.Released} - {movie.Runtime}
          </p>
        </div>
      </div>
      <div className="movie-rating-box">
        <p>Rating Stars</p>
        <Button className="btn-add" click={() => handleAddMovie(movie.imdbID)}>
          Add Movie
        </Button>
      </div>
      <div className="movie-details-desc">
        <p>{movie.Plot}</p>
        <p className="actors">Staring: {movie.Actors}</p>
        <p className="director">Director: {movie.Director}</p>
      </div>
    </div>
  );
}

function WatchedListMovies({ watchedList }) {
  return (
    <>
      <ul>
        {watchedList.map((movie) => (
          <WatchedMovie key={movie.imdbID} movie={movie} />
        ))}
      </ul>
    </>
  );
}

function WatchedMovie({ movie, handleMovieDelete }) {
  return (
    <li className="watched-movie">
      <img src={movie.Poster} alt={movie.Title} />
      <div className="watched-movie-details">
        <h5>{movie.Title}</h5>
        <div className="watched-movie-props">
          <div>
            <div>⭐ {movie.Rating}</div>
            <div>🌟 x.x</div>
            <div>⏲️ {movie.Runtime}</div>
          </div>
          <Button
            className="btn-delete"
            click={() => handleMovieDelete(movie.imdbID)}
          >
            x
          </Button>
        </div>
      </div>
    </li>
  );
}

export default MainApp;
