import React, { useReducer, useState } from "react";
import "./Product.css";

const currencyOptions = {
    minimumFractionDigits: 2,
    maximumFractionDigits: 2
}

function getTotal(total) {
    return total.toLocaleString(undefined, currencyOptions)
}

const products = [
    {
        emoji: '🍦',
        name: 'ice cream',
        price: 5
    },
    {
        emoji: '🍩',
        name: 'donuts',
        price: 2.5,
    },
    {
        emoji: '🍉',
        name: 'watermelon',
        price: 4
    }
];

function cartReducer(state, product) {
    return [...state, product]
}

function totalReducer(state, price) {
    return state + price
}

function Product() {
    const [cart, setCart] = useReducer(cartReducer, [])
    const [total, setTotal] = useReducer(totalReducer, 0)

    const add = (product) => {
        setCart(product.name)
        setTotal(currentPrice => currentPrice + product.price)
    }

    const remove = (product) => {

        // setCart(prevState => {
        //     const cart = [...prevState]
        //     const prodIndex = cart.findIndex(p => p.name === product.name);
        //     if (prodIndex < 1) return;
        //     cart.splice(prodIndex, 1)
        //     return cart
        // });
        // setTotal(currentPrice => currentPrice - product.price)
    }

    return (
        <div className="wrapper">
            <div>
                Shopping cart: {cart.length} total {cart.length > 1 ? 'items' : 'item'}
            </div>
            <div>
                Total: {getTotal(total)}
            </div>
            {products.map(product => {
                return (
                    <div className="product">
                        <span role="img" aria-label={product.name}>{product.emoji}</span>
                        <p>Price: {product.price}</p>
                        <button onClick={() => add(product)}>Add</button>
                        <button onClick={() => remove(product)}>Remove</button>
                    </div>
                )
            })}

        </div>
    )
}


export default Product
