import { useState } from "react";

import "./Faraway.css";

const initialItems = [
    { id: 1, description: "Passports", quantity: 2, packed: false },
    { id: 2, description: "Socks", quantity: 12, packed: false },
];

function Main() {

    const [item, setItem] = useState([]);

    const handleSubmit = (e) => {
        e.prevent.Default();
        setItem(prevItem => {
            if (e.target.value === '') return;

        })
    }

    return (
        <div>
            <Header />
            <Menu />
            <PackingList handleAdd={handleSubmit} />
            <Footer />
        </div>
    )
}

function Header() {
    return (
        <div className="header">
            <h1>üå≥ Far Way üíº</h1>
        </div>
    )
}

function Menu() {

    return (
        <form className="menu">
            <p>What do you want to go üõ´ with </p>
            <select name="itenNum" id="itemNum" className="selectOption">
                <option value="1">1</option>
                <option value="2">2</option>
                <option value="3">3</option>
                <option value="4">4</option>
                <option value="5">5</option>
            </select>
            <input
                type="text"
                name="itemName"
                id="itemName"
                className="inputItem"
                placeholder="Enter the item Name"

            />
            <div>
                <button className="btnAdd">Add</button>
            </div>
        </form>
    )

}

function PackingList() {

    return (
        <div className="container">
            <ul className="parkingList">
                {initialItems.map(item => (
                    <Item
                        quantity={item.quantity}
                        description={item.description}
                    />
                ))}
            </ul>
        </div>
    )
}

function Item({ quantity, description }) {
    return (
        <li className="items">
            <span>{quantity}</span>
            <p>{description}</p>
            <span className="btn-delete">‚úñÔ∏è</span>
        </li>
    )
}

function Stats() {
    return (
        <>

        </>
    )
}


function Footer() {
    return (
        <div className="footer">
            <p>Footer Section</p>
        </div>
    )
}

export default Main;
