import { useState } from "react";
import "./TipCalculator.css";

function TipCalculator() {
  const [amount, setAmount] = useState(0);

  const handleAmount = (e) => {
    setAmount(e.target.value);
  };

  return (
    <div className="container">
      <InputBill amount={amount} handleAmount={handleAmount} />
      <ServiceBill text="How did you like the service?">
        <select>
          <option>Dissertisfied(0%)</option>
          <option>It was ok (5%)</option>
          <option>It was good (10%)</option>
          <option>It was supper cool (15%)</option>
        </select>
      </ServiceBill>
      <ServiceBill text="How did your friend like the service?">
        <select>
          <option>Dissertisfied(0%)</option>
          <option>Not Bad (5%)</option>
          <option>Amzing(10%)</option>
          <option>Excellent(15%)</option>
        </select>
      </ServiceBill>

      <OutputBill bill={amount} />
    </div>
  );
}

function InputBill({ amount, handleAmount }) {
  return (
    <div className="content">
      <p>How much was the bill?</p>
      <input
        type="text"
        name="bill"
        placeholder="enter the bill"
        value={amount}
        onChange={handleAmount}
      />
    </div>
  );
}

function ServiceBill({ text, children }) {
  return (
    <div className="content">
      <p>{text}</p>
      {children}
    </div>
  );
}

function OutputBill({ bill }) {
  return (
    <>
      <p className="output">You Pay $0 (`${bill} + $0`) tip</p>
    </>
  );
}

export default TipCalculator;
