import { useState } from "react";

import "./Faraway.css";

// const initialItems = [
//     { id: 1, description: "Passports", quantity: 2, packed: false },
//     { id: 2, description: "Socks", quantity: 12, packed: false },
// ];

function Main() {

    const [list, setList] = useState([])
    const [description, setDescription] = useState("");
    const [quantity, setQuantity] = useState(1);

    const handleSubmit = (e) => {
        e.preventDefault();

        const newItem = { description, quantity, packed: false, id: Date.now() }
        // console.log(newItem);

        setList(prevList => [
            ...prevList, newItem
        ]);

        setQuantity(1);
        setDescription("")
    }

    const handleDescription = e => {
        setDescription(e.target.value)
    }

    const handleQuantity = e => {
        setQuantity(e.target.value)
    }

    return (
        <div>
            <Header />
            <Form
                handleSubmit={handleSubmit}
                description={description}
                handleDescription={handleDescription}
                handleQuantity={handleQuantity}
                quantity={quantity}
                list={list}
            />
            <PackingList
                handleSubmit={handleSubmit}
                description={description}
                handleDescription={handleDescription}
                handleQuantity={handleQuantity}
                quantity={quantity}
                list={list}
            />
            <Footer />
        </div>
    )
}

function Header() {
    return (
        <div className="header">
            <h1>🌳 Far Way 💼</h1>
        </div>
    )
}

function Form({ handleSubmit, handleQuantity, quantity, description, handleDescription }) {

    // const [description, setDescription] = useState([]);
    // const [quantity, setQuantity] = useState(1);

    // const handleSubmit = (e) => {
    //     e.preventDefault();

    //     const newItem = { description, quantity, packed: false, id: Date.now() }
    //     console.log(newItem);
    // }

    return (
        <form className="menu" onSubmit={handleSubmit}>
            <p>What do you need 🥰 for your trip </p>
            <select
                name="itenNum"
                id="itemNum"
                className="selectOption"
                value={quantity}
                onChange={handleQuantity}
            >
                {Array.from({ length: 20 }, (_, i) => i + 1).map((num) => (
                    <option value={num} key={num}>
                        {num}
                    </option>
                ))}
            </select>
            <input
                type="text"
                name="itemName"
                id="itemName"
                className="inputItem"
                value={description}
                onChange={handleDescription}
            />
            <div>
                <button className="btnAdd">Add</button>
            </div>
        </form>
    )

}

function PackingList({ list }) {

    return (
        <div className="container">
            <ul className="parkingList">
                {list.map((item) => (
                    <Item
                        quantity={item.quantity}
                        description={item.description}
                        key={item.id}
                        packed={item.packed}
                    />
                ))}
            </ul>
        </div>
    )
}

function Item({ quantity, description }) {
    return (
        <li className="items" onClick={() => { }}>
            <span>{quantity}</span>
            <p>{description}</p>
            <span className="btn-delete">✖️</span>
        </li>
    )
}

// function Stats() {
//     return (
//         <>

//         </>
//     )
// }


function Footer() {
    return (
        <div className="footer">
            <p>Footer Section</p>
        </div>
    )
}

export default Main;
