import React from "react";
import "./Weather.css";

class Weather extends React.Component {

    componentDidMount() {
        getWeather('Abuja');
    }

    render() {
        return <React.Fragment>
            <div className="app">
                <h1>Classy Weather</h1>
                <input type="text" />
            </div>
        </React.Fragment>
    }
}

export default Weather;

function getWeatherIcon(wmoCode) {
