import { useState } from "react";

import "./Faraway.css";

// const initialItems = [
//     { id: 1, description: "Passports", quantity: 2, packed: false },
//     { id: 2, description: "Socks", quantity: 12, packed: false },
// ];

function Main() {

    const [description, setDescription] = useState([]);
    const [quantity, setQuantity] = useState(1);


    const handleSubmit = (e) => {
        e.preventDefault();

        if (!description) return;

        const newItem = { description, quantity, packed: false, id: Date.now() };

        setDescription(prevItem => [
            ...prevItem, newItem
        ]);

        setDescription("")
        setQuantity(1);
    }

    const handleDescription = (e) => {
        setDescription(e.target.value)
    }

    const handleQuantity = (e) => {
        setQuantity(e.target.value)
    }

    return (
        <div>
            <Header />
            <Menu
                handleSubmit={handleSubmit}
                handleChange={handleDescription}
                items={description}
                handleQuantity={handleQuantity}
            />
            <PackingList items={description} />
            <Footer />
        </div>
    )
}

function Header() {
    return (
        <div className="header">
            <h1>üå≥ Far Way üíº</h1>
        </div>
    )
}

function Menu({ handleSubmit, handleDescription, handleQuantity, items }) {

    return (
        <form className="menu" onSubmit={handleSubmit}>
            <p>What do you need ü•∞ for your trip </p>
            <select name="itenNum" id="itemNum" className="selectOption" onChange={handleQuantity}>
                {Array.from({ length: 20 }, (_, i) => i + 1).map((num) => (
                    <option value={num} key={num}>
                        {num}
                    </option>
                ))}
            </select>
            <input
                type="text"
                name="itemName"
                id="itemName"
                className="inputItem"
                onChange={handleDescription}
                value={items}
            />
            <div>
                <button className="btnAdd">Add</button>
            </div>
        </form>
    )

}

function PackingList({ items }) {

    return (
        <div className="container">
            <ul className="parkingList">
                {items.map((item) => (
                    <Item
                        quantity={item.quantity}
                        description={item.description}
                        id={item.id}
                    />
                ))}
            </ul>
        </div>
    )
}

function Item({ quantity, description, id }) {
    return (
        <li className="items" key={id}>
            <span>{quantity}</span>
            <p>{description}</p>
            <span className="btn-delete">‚úñÔ∏è</span>
        </li>
    )
}

// function Stats() {
//     return (
//         <>

//         </>
//     )
// }


function Footer() {
    return (
        <div className="footer">
            <p>Footer Section</p>
        </div>
    )
}

export default Main;
