import { useState } from "react";
// import {addDays } from "date-fns";
import "./DaysDate.css";

function addDays(date, days) {
    const dateCopy = new Date(date);
    dateCopy.setDate(dateCopy.getDate() + days);
    // const actualDate = d.split('T')[0];
    return dateCopy;
}

function Counter() {

    const [count, setCount] = useState(0);
    const [step, setStep] = useState(0)

    const handleIncrease = () => {
        setCount(prevCount => prevCount + 1);
    }
    const handleDecrease = () => {
        setCount(prevCount => prevCount - 1);
    }

    const handleStepIncrease = () => setStep((step) => step + 1);

    const handleStepDecrease = () => setStep((step) => step - 1);

    let today = new Date().toISOString().slice(0, 10);

    return (
        <>
            <div className="container">
                <div className="step">
                    <button onClick={handleStepDecrease}>-</button>
                    <p>Step: {step}</p>
                    <button onClick={handleStepIncrease}>+</button>
                </div>
                <div className="counter">
                    <button onClick={handleDecrease}>-</button>
                    <p>Counter: {count}</p>
                    <button onClick={handleIncrease}>+</button>

                </div>
                <p>{count} Days from today is {addDays(new Date('june 27 2027'), count)}</p>
            </div>
        </>
    )
}

export default Counter;
