import React, { useEffect, useState } from "react";
import "./popcorn.css";

const KEY = "6b44cc4";

function MainApp() {
  const [query, setQuery] = useState("Fire proof");

  const handleChangeQuery = (e) => {
    const q = e.target.value;
    setQuery(q);
  };

  return (
    <div className="container">
      <NavBar>
        <Logo />
        <SearchBar query={query} handleChangeQuery={handleChangeQuery} />
        <Notification />
      </NavBar>
      <Main query={query} />
    </div>
  );
}

function NavBar({ children }) {
  return <div className="navbar">{children}</div>;
}

function Logo() {
  return (
    <div className="logo">
      <p>
        <span>üçø</span>usePopcorn
      </p>
    </div>
  );
}

function SearchBar({ query, handleChangeQuery }) {
  return (
    <div className="search">
      <form>
        <input
          type="text"
          placeholder="Search movies"
          value={query}
          onChange={handleChangeQuery}
        />
      </form>
    </div>
  );
}

function Notification() {
  return (
    <div className="notification">
      <span>Found X Result</span>
    </div>
  );
}

function Main({ query }) {
  const [movies, setMovies] = useState([]);
  const [movieId, setMovieId] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [hasError, setHasError] = useState("");

  const handleSelectedMovie = (id) => {
    setMovieId(id);
  };

  useEffect(
    function () {
      try {
        setIsLoading(true);
        async function getMovies() {
          const res = await fetch(
            `http://www.omdbapi.com/?apikey=${KEY}&s=${query}`
          );
          const data = await res.json();
          setIsLoading(false);
          setMovies(data.Search);
        }

        getMovies();
      } catch (error) {
        const err = "‚õî Error fetching Movie";
        setHasError(err);
      }
    },
    [query]
  );

  return (
    <div className="content">
      <Box className="box">
        {isLoading ? (
          <Loader />
        ) : !hasError ? (
          <Error message={hasError} />
        ) : (
          <MoviesList
            movies={movies}
            handleSelectedMovie={handleSelectedMovie}
          />
        )}

        {/* {!hasError ? (
          <Error message={hasError} />
        ) : (
          <MoviesList
            movies={movies}
            handleSelectedMovie={handleSelectedMovie}
          />
        )} */}
      </Box>
      <Box className="box">
        {movieId === null ? <BoxHeader /> : <MovieDetails movieId={movieId} />}
      </Box>
    </div>
  );
}

function Box({ children, className }) {
  return <div className={className}>{children}</div>;
}

function BoxHeader() {
  return (
    <header className="box-header">
      <h4>Movies You Watched</h4>
      <div className="movies-rec">
        <div className="minutes">
          <div>
            <span>#Ô∏è‚É£</span>
            <span>X Movies</span>
          </div>
        </div>
        <div>
          <span>‚≠ê </span>
          <span>x.x</span>
        </div>
        <div>
          <span>üåü </span>
          <span>x.x</span>
        </div>
        <div>
          <span>‚è≤Ô∏è </span>
          <span>x.x min</span>
        </div>
      </div>
    </header>
  );
}

function Loader() {
  return (
    <div className="loader">
      <span>Loading . . .</span>
    </div>
  );
}

function Error({ message }) {
  return (
    <div className="error">
      <span>{message}</span>
    </div>
  );
}

function MoviesList({ movies, handleSelectedMovie }) {
  return (
    <ul>
      {movies.map((movie, key) => {
        return (
          <Movie
            key={movie.imdbID}
            movie={movie}
            handleSelectedMovie={handleSelectedMovie}
          />
        );
      })}
    </ul>
  );
}

function Movie({ movie, handleSelectedMovie }) {
  return (
    <li className="movie" onClick={() => handleSelectedMovie(movie.imdbID)}>
      <img src={movie.Poster} alt={movie.Title} />
      <div className="movieDetails">
        <p>{movie.Title}</p>
        <span>üóìÔ∏è {movie.Year}</span>
      </div>
    </li>
  );
}

function MovieDetails({ movieId }) {
  const [movie, setMovie] = useState([]);

  useEffect(
    function () {
      async function getSelectedMovie() {
        const res = await fetch(
          `http://www.omdbapi.com/?apikey=${KEY}&i=${movieId}`
        );
        const data = await res.json();
        console.log(data);
        setMovie(data);
      }

      getSelectedMovie();
    },
    [movieId]
  );

  return (
    <div className="movie-details-container">
      <div className="movie-details-header">
        <img src={movie.Poster} alt={movie.Title} />
        <div className="movie-details-title">
          <h3>{movie.Title}</h3>
          <p>
            {movie.Released} - {movie.Runtime}
          </p>
        </div>
      </div>
      <div className="movie-rating-box">rating lists</div>
      <div className="movie-details-desc">
        <p>{movie.Plot}</p>
        <p className="actors">Staring: {movie.Actors}</p>
        <p className="director">Director: {movie.Director}</p>
      </div>
    </div>
  );
}

export default MainApp;
