import React from "react";
import "./Course.css"

class Course extends React.Component {

    constructor() {
        super()
        this.state = {
            step: 1,
            messages: [
                "Learn React",
                "Apply for Job",
                "Invest your Income"
            ]
        }
    }

    handlePrevious = () => {
        if (this.state.step > 1) {
            return this.setState({ step: this.state.step - 1 })
        }
    }

    handleNext = () => {
        if (this.state.step < 3) {
            return this.setState({ step: this.state.step + 1 });
        }
    }

    render() {
        return (
            <>
                <div className="container">
                    <div className="section">
                        <div className="section_number">
                            <p className={this.state.step >= 1 && 'active'}>1</p>
                            <p className={this.state.step >= 2 ? 'active' : ''}>2</p>
                            <p className={this.state.step >= 3 ? 'active' : ''}>3</p>
                        </div>

                        <div className="message">
                            <p>{this.state.messages[this.state.step - 1]}</p>
                        </div>
                        <div className="navigation">
                            <button className="btn" onClick={this.handlePrevious}>Previous</button>
                            <button className="btn" onClick={this.handleNext}>Next</button>
                        </div>
                    </div>

                </div>
            </>
        )
    }
}

export default Course;
