import { useState } from "react";

import "./Faraway.css";

// const initialItems = [
//     { id: 1, description: "Passports", quantity: 2, packed: false },
//     { id: 2, description: "Socks", quantity: 12, packed: false },
// ];

function Main() {

    const [list, setList] = useState([])
    const [description, setDescription] = useState("");
    const [quantity, setQuantity] = useState(1);
    const [toggle, setToggle] = useState(false)
    // const [packed, setPacked] = useState(false)

    const count = list.length;
    const countToggle = list.filter(item => item.packed).length;
    const percent = (countToggle / count) * 100;

    const handleSubmit = (e) => {
        e.preventDefault();

        const newItem = { description, quantity, packed: false, id: Date.now() }
        // console.log(newItem);

        setList(prevList => [
            ...prevList, newItem
        ]);

        setQuantity(1);
        setDescription("")
    }

    const handleDescription = e => {
        setDescription(e.target.value)
    }

    const handleQuantity = e => {
        setQuantity(e.target.value)
    }

    const handleDelete = (id) => {
        const fileteredList = list.filter(item => item.id !== id);
        setList(fileteredList);
    }

    const handleToggle = id => {
        // const newList = list.filter(item => item.id === id)

    }

    return (
        <div>
            <Header />
            <Form
                handleSubmit={handleSubmit}
                description={description}
                handleDescription={handleDescription}
                handleQuantity={handleQuantity}
                quantity={quantity}
                list={list}
            />
            <PackingList
                handleSubmit={handleSubmit}
                list={list}
                handleDelete={handleDelete}
                toggle={toggle}
                handleToggle={handleToggle}
            />
            <Stats count={count} countToggle={countToggle} percent={percent} />
            <Footer />
        </div>
    )
}

function Header() {
    return (
        <div className="header">
            <h1>🌳 Far Way 💼</h1>
        </div>
    )
}

function Form({ handleSubmit, handleQuantity, quantity, description, handleDescription }) {

    return (
        <form className="menu" onSubmit={handleSubmit}>
            <p>What do you need 🥰 for your trip </p>
            <select
                name="itenNum"
                id="itemNum"
                className="selectOption"
                value={quantity}
                onChange={handleQuantity}
            >
                {Array.from({ length: 20 }, (_, i) => i + 1).map((num) => (
                    <option value={num} key={num}>
                        {num}
                    </option>
                ))}
            </select>
            <input
                type="text"
                name="itemName"
                id="itemName"
                className="inputItem"
                value={description}
                onChange={handleDescription}
            />
            <div>
                <button className="btnAdd">Add</button>
            </div>
        </form>
    )

}

function PackingList({ list, handleDelete, toggle, handleToggle }) {


    return (
        <div className="container">
            <ul className="parkingList">
                {list.map((item) => (
                    <Item
                        quantity={item.quantity}
                        description={item.description}
                        id={item.id}
                        packed={item.packed}
                        handleDelete={handleDelete}
                        toggle={toggle}
                        handleToggle={handleToggle}
                    />
                ))}
            </ul>
        </div>
    )
}

function Item({ quantity, description, id, packed, handleDelete, handleToggle }) {

    return (

        <div className="itemContainer">

            <li className={packed ? "packed" : ""} key={id} >
                <input type="checkbox" value={packed} name="checkSelect" onClick={() => handleToggle(id)} />
                <span>{quantity}</span>
                <p>{description}</p>
                <span className="btn-delete" onClick={() => handleDelete(id)} >x</span>
            </li >

        </div>

    )
}

function Stats({ count }) {
    return (
        <>
            <p className="stats">
                You have {count} items on your list, and you already packed Y (Z%)
            </p>
        </>
    )
}


function Footer() {
    return (
        <div className="footer">
            <p>Copyright (C) {new Date().getFullYear()} U-3 Technologies Inc.</p>
        </div>
    )
}

export default Main;
