function Main({ query, movies, setMovies }) {
  // const [movies, setMovies] = useState([]);
  const [movieId, setMovieId] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [hasError, setHasError] = useState("");
  const [watchedList, setWatchedList] = useState([]);
  const [showWatched, setShowWatched] = useState(false);
  const [movie, setMovie] = useState([]);
  const [isClose, setIsClose] = useState(null);

  const handleClose = () => {
    setIsClose(!isClose);
    setMovieId(null);
    console.log(movieId);
  };
  const handleSelectedMovie = (id) => {
    setMovieId(id);
    console.log(id);
  };
  const err = "‚õî Error fetching Movie";

  const handleAddMovie = (id) => {
    setWatchedList((prevMovies) => [...prevMovies, movie]);
    setShowWatched(true);
  };

  useEffect(
    function () {
      setIsLoading(true);

      async function getMovies() {
        try {
          const res = await fetch(
            `http://www.omdbapi.com/?apikey=${KEY}&s=${query}`
          );
          if (!res.ok) throw new Error("Something went wrong fetching movies");

          const data = await res.json();
          if (data.Response === "False") throw new Error("Movie not found");

          setMovies(data.Search);
          // console.log(data.Search);
          setHasError("");
        } catch (error) {
          if (err.name !== "AbortError") {
            setHasError(error.message);
          }
        } finally {
          setIsLoading(false);
        }
      }

      if (query.length < 3) {
        setMovies([]);
        setHasError("");
        return;
      }

      getMovies();
    },
    [query, setMovies]
  );

  return (
    <div className="content">
      <Box className="box">
        {isLoading && <Loader />}

        {!isLoading && !hasError && (
          <MoviesList
            movies={movies}
            handleSelectedMovie={handleSelectedMovie}
          />
        )}

        {!hasError && <Error message={hasError} />}
      </Box>
      <Box className="box">
        {movieId === null || isClose ? (
          <Summary />
        ) : showWatched ? (
          <>
            <Summary /> <WatchedListMovies watchedList={watchedList} />
          </>
        ) : (
          <MovieDetails
            movieId={movieId}
            handleAddMovie={handleAddMovie}
            movie={movie}
            setMovie={setMovie}
            onclose={handleClose}
            key={movieId}
          />
        )}
      </Box>
    </div>
  );
}

