import React, { useState } from "react";
import { useFetch } from "./useFetch";
import "./Weather.css";

function Weather() {
  const [city, setCity] = useState("Abuja");
  const [country, setCountry] = useState("");

  const handleCity = (e) => {
    setCity(e.target.value);
  };

  return (
    <div className="container">
      <div className="content">
        <h2>Classy Weather</h2>
        <Input city={city} handleCity={handleCity} />
        <h2>Weather {country && country}</h2>
        <WeatherDetails city={city} setCountry={setCountry} />
      </div>
    </div>
  );
}

function Input({ city, handleCity }) {
  return (
    <div className="city-input">
      <input
        type="text"
        placeholder="City Name"
        value={city}
        onChange={handleCity}
      />
    </div>
  );
}

// function Loader() {
//   return (
//     <>
//       <p className="loader">Loading...</p>
//     </>
//   );
// }

function WeatherDetails({ city, setCountry }) {
  const [data, country, code] = useFetch(city);
  setCountry(country);
  const { time, w_code, maxTemp, minTemp } = data;
  // console.log(data);
  return (
    <ul className="weather-list">
      {
        <li className="weather-item">
          <span>☁️</span>
          <p>Day Name</p>
          <p>Temparature</p>
        </li>
      }
    </ul>
  );
}

function Detail() {
  return (
    <li className="weather-item">
      <span>☁️</span>
      <p>Day Name</p>
      <p>Temparature</p>
    </li>
  );
}

export default Weather;
