import React, { useEffect } from "react";

export default function Timer({ dispatch, minSecond }) {
  const mins = Math.ceil(minSecond / 60);
  const sec = mins % 60;
  useEffect(
    function () {
      const id = setInterval(function () {
        dispatch({ type: "tick" });
      }, 1000);
      return clearInterval(() => id);
    },
    [dispatch]
  );
  return <div className="btn-start btn-next"></div>;
}
