import "./Weather.css";

function Weather() {
  return (
    <div className="container">
      <div className="content">
        <h2>Classy Weather</h2>
        <Input />
      </div>
    </div>
  );
}

function Input() {
  return (
    <div className="city-input">
      <input type="text" placeholder="City Name" />
    </div>
  );
}

function Loader() {
  return (
    <>
      <p className="loader">Loading...</p>
    </>
  );
}

function WeatherData() {
  return <div></div>;
}

export default Weather;
