import { useEffect, useState } from "react";

export function useLocation(city) {
  const { location, setLocation } = useState({
    latitude: 0,
    longitude: 0,
    timezone: "",
    name: "",
    country_code: "",
  });
  useEffect(
    function () {
      async function getLocation() {
        const res = await fetch(
          `https://geocoding-api.open-meteo.com/v1/search?name=${city}`
        );
        const result = res.json();
        console.log(result);

        if (result.results) throw new Error("Location not found");

        setLocation(result.results.at(0));
      }

      getLocation();
    },

    [city, setLocation]
  );

  return [
    location.latitude,
    location.longitude,
    location.timezone,
    location.name,
    location.country_code,
  ];
}
