import React from "react";

export default function Options({ options, dispatch, answer }) {
  return (
    <>
      {options.map((option, index) => (
        <div className="option">
          <button
            key={index}
            onClick={() => dispatch({ type: "newAnswer", payload: index })}
          >
            {option}
          </button>
        </div>
      ))}
    </>
  );
}
