import React from "react";

export default function Options({ options, dispatch, answer }) {
  return (
    <>
      {options.map((option, index) => (
        <div
          className="options"
          key={index}
          onClick={() => dispatch({ type: "newAnswer", payload: index })}
        >
          {option}
        </div>
      ))}
    </>
  );
}
