import React from "react";
import "./popcorn.css";

function MainApp() {
  return (
    <div>
      <NavBar />
      <Main />
    </div>
  );
}

function NavBar() {
  return (
    <div className="navbar">
      <Logo />
      <SearchBar />
      <Notification />
    </div>
  );
}

function Logo() {
  return (
    <div className="logo">
      <span>üçø</span>
      <p>usePopcorn</p>
    </div>
  );
}

function SearchBar() {
  return (
    <div className="search">
      <input type="text" placeholder="Search movies" />
    </div>
  );
}

function Notification() {
  return (
    <div className="notification">
      <span>Found X Result</span>
    </div>
  );
}

function Main() {
  return <></>;
}

export default MainApp;
