import React, { useState } from "react";
import "./Currency.css";

function Main() {
  const [amount, setAmount] = useState("");
  const [to, setTo] = useState("USD");
  const [from, setFrom] = useState("NGN");

  return (
    <div className="container">
      <div className="main">
        <Input amount={amount} />
        <CurrencyType>
          <select>
            <option>NGN</option>
            <option>USD</option>
            <option>EUR</option>
          </select>
        </CurrencyType>
        <CurrencyType>
          <select>
            <option>NGN</option>
            <option>USD</option>
            <option>EUR</option>
          </select>
        </CurrencyType>
      </div>

      <Output />
    </div>
  );
}

function Input({ amount }) {
  return (
    <>
      <input type="text" name="amount" value={amount} placeholder="amount" />
    </>
  );
}

function CurrencyType({ children }) {
  return <>{children}</>;
}

function Output() {
  return <p>OUTPUT</p>;
}

export default Main;
