import { useState } from "react";
import "./eat.css";

const initialFriends = [
  {
    id: 118836,
    name: "Clark",
    image: "https://i.pravatar.cc/48?u=118836",
    balance: -7,
  },
  {
    id: 933372,
    name: "Sarah",
    image: "https://i.pravatar.cc/48?u=933372",
    balance: 20,
  },
  {
    id: 499476,
    name: "Anthony",
    image: "https://i.pravatar.cc/48?u=499476",
    balance: 0,
  },
];

function Button({ children, onClick }) {
  return (
    <button className="btn" onClick={onClick}>
      {children}
    </button>
  );
}

export default function Main() {
  const [showAddFriend, setShowAddFriend] = useState(false);
  const [name, setName] = useState("");
  const [image, setImage] = useState("https://i.pravatar.cc/48");

  function handleShowAddFriend() {
    setShowAddFriend((show) => !show);
  }

  function handleSetName() {
    setName();
  }

  function handleSetImage() {
    setImage();
  }

  return (
    <div className="container">
      <div className="sidebar">
        <FriendsList friends={initialFriends} />
        {showAddFriend && (
          <FormAddFriends
            name={name}
            image={image}
            handleSetName={handleSetName}
            handleSetImage={handleSetImage}
          />
        )}
        <div className="btn-add">
          <Button onClick={handleShowAddFriend}>
            {showAddFriend ? "Close" : "Add friend"}
          </Button>
        </div>
      </div>
      <BillForm />
    </div>
  );
}

function FriendsList({ friends }) {
  return (
    <ul>
      {friends.map((friend) => (
        <Friends key={friend.balance} friend={friend} />
      ))}
    </ul>
  );
}

function Friends({ friend }) {
  return (
    <li className="friendList">
      <img src={friend.image} alt={friend.name} />
      <p>
        <h4>{friend.name}</h4>
        <p> You owe Clark $7</p>
      </p>
      <Button>Select</Button>
    </li>
  );
}

function FormAddFriends({ name, handleSetName, image, handleSetImage }) {
  return (
    <form className="addForm">
      <div className="addForm-fields">
        <label>üßë‚Äçü§ù‚ÄçüßëFriend name</label>
        <input type="text" value={name} onChange={handleSetName} />
      </div>

      <div className="addForm-fields">
        <label>üñºÔ∏èImage name</label>
        <input type="text" value={image} onChange={handleSetImage} />
      </div>

      <Button>Add</Button>
    </form>
  );
}

function BillForm() {
  return <h1>Bill Section</h1>;
}
