import { useState } from "react";

import "./Faraway.css";

// const initialItems = [
//     { id: 1, description: "Passports", quantity: 2, packed: false },
//     { id: 2, description: "Socks", quantity: 12, packed: false },
// ];

function Main() {

    const [list, setList] = useState([]);
    const [item, setItem] = useState("")


    const handleSubmit = (e) => {
        e.preventDefault();

        if (list === "") return;

        setList(prevList => [
            ...prevList, {
                id: new Date(),
                description: e.target.value,
                quantity: 2,
                packed: false
            }
        ]);

        console.log(list);
    }

    const handleChange = (e) => {
        setItem(e.target.value)
    }

    return (
        <div>
            <Header />
            <Menu handleSubmit={handleSubmit} handleChange={handleChange} item={item} />
            <PackingList item={list} />
            <Footer />
        </div>
    )
}

function Header() {
    return (
        <div className="header">
            <h1>üå≥ Far Way üíº</h1>
        </div>
    )
}

function Menu({ handleSubmit, handleChange, item }) {

    return (
        <form className="menu" onSubmit={handleSubmit}>
            <p>What do you want to go üõ´ with </p>
            <select name="itenNum" id="itemNum" className="selectOption">
                <option value="1">1</option>
                <option value="2">2</option>
                <option value="3">3</option>
                <option value="4">4</option>
                <option value="5">5</option>
            </select>
            <input
                type="text"
                name="itemName"
                id="itemName"
                className="inputItem"
                onChange={handleChange}
                value={item}
            />
            <div>
                <button className="btnAdd">Add</button>
            </div>
        </form>
    )

}

function PackingList({ item }) {

    return (
        <div className="container">
            <ul className="parkingList">
                {item.map((item, index) => (
                    <Item
                        quantity={item.quantity}
                        description={item.description}
                        key={index}
                    />
                ))}
            </ul>
        </div>
    )
}

function Item({ quantity, description, key }) {
    return (
        <li className="items" key={key}>
            <span>{quantity}</span>
            <p>{description}</p>
            <span className="btn-delete">‚úñÔ∏è</span>
        </li>
    )
}

// function Stats() {
//     return (
//         <>

//         </>
//     )
// }


function Footer() {
    return (
        <div className="footer">
            <p>Footer Section</p>
        </div>
    )
}

export default Main;
