import React, { useEffect, useState } from "react";
import "./popcorn.css";

const tempMovieData = [
  {
    imdbID: "tt1375666",
    Title: "Inception",
    Year: "2010",
    Poster:
      "https://m.media-amazon.com/images/M/MV5BMjAxMzY3NjcxNF5BMl5BanBnXkFtZTcwNTI5OTM0Mw@@._V1_SX300.jpg",
  },
  {
    imdbID: "tt0133093",
    Title: "The Matrix",
    Year: "1999",
    Poster:
      "https://m.media-amazon.com/images/M/MV5BNzQzOTk3OTAtNDQ0Zi00ZTVkLWI0MTEtMDllZjNkYzNjNTc4L2ltYWdlXkEyXkFqcGdeQXVyNjU0OTQ0OTY@._V1_SX300.jpg",
  },
  {
    imdbID: "tt6751668",
    Title: "Parasite",
    Year: "2019",
    Poster:
      "https://m.media-amazon.com/images/M/MV5BYWZjMjk3ZTItODQ2ZC00NTY5LWE0ZDYtZTI3MjcwN2Q5NTVkXkEyXkFqcGdeQXVyODk4OTc3MTY@._V1_SX300.jpg",
  },
];

const tempWatchedData = [
  {
    imdbID: "tt1375666",
    Title: "Inception",
    Year: "2010",
    Poster:
      "https://m.media-amazon.com/images/M/MV5BMjAxMzY3NjcxNF5BMl5BanBnXkFtZTcwNTI5OTM0Mw@@._V1_SX300.jpg",
    runtime: 148,
    imdbRating: 8.8,
    userRating: 10,
  },
  {
    imdbID: "tt0088763",
    Title: "Back to the Future",
    Year: "1985",
    Poster:
      "https://m.media-amazon.com/images/M/MV5BZmU0M2Y1OGUtZjIxNi00ZjBkLTg1MjgtOWIyNThiZWIwYjRiXkEyXkFqcGdeQXVyMTQxNzMzNDI@._V1_SX300.jpg",
    runtime: 116,
    imdbRating: 8.5,
    userRating: 9,
  },
];

const KEY = "6b44cc4";

function MainApp() {
  return (
    <div className="container">
      <NavBar />
      <Main />
    </div>
  );
}

function NavBar() {
  return (
    <div className="navbar">
      <Logo />
      <SearchBar />
      <Notification />
    </div>
  );
}

function Logo() {
  return (
    <div className="logo">
      <p>
        <span>üçø</span>usePopcorn
      </p>
    </div>
  );
}

function SearchBar() {
  return (
    <div className="search">
      <input type="text" placeholder="Search movies" />
    </div>
  );
}

function Notification() {
  return (
    <div className="notification">
      <span>Found X Result</span>
    </div>
  );
}

function Main() {
  const [movieList, setMovieList] = useState([]);
  // const [movies, setMovies] = useState([]);

  const handleSelectedMovie = (id) => {
    const movie = tempMovieData.filter((movie) => movie.imdbID === id);

    setMovieList(movie[0]);
  };

  useEffect(function () {
    async function getMovies() {
      const res = await fetch(`http://www.omdbapi.com/?apikey=${KEY}&t=matrix`);
      const data = await res.json();
      console.log(data);
    }

    getMovies();
  }, []);

  return (
    <div className="content">
      <Box className="box">
        <MovieList
          movies={tempMovieData}
          handleSelectedMovie={handleSelectedMovie}
          selectedMovie={movieList}
        />
      </Box>
      <Box className="box">
        {/* {movieList ? <BoxHeader /> : <MovieDetails movie={movieList} />} */}
        <MovieDetails movie={movieList} />
      </Box>
    </div>
  );
}

function Box({ children, className }) {
  return <div className={className}>{children}</div>;
}

function BoxHeader() {
  return (
    <header className="box-header">
      <h4>Movies You Watched</h4>
      <div className="movies-rec">
        <div className="minutes">
          <div>
            <span>#Ô∏è‚É£</span>
            <span>X Movies</span>
          </div>
        </div>
        <div>
          <span>‚≠ê </span>
          <span>x.x</span>
        </div>
        <div>
          <span>üåü </span>
          <span>x.x</span>
        </div>
        <div>
          <span>‚è≤Ô∏è </span>
          <span>x.x min</span>
        </div>
      </div>
    </header>
  );
}

function MovieList({ movies, handleSelectedMovie, selectedMovie }) {
  return (
    <ul>
      {movies.map((movie, key) => {
        return (
          <Movie
            key={movie.imdbID}
            movie={movie}
            handleSelectedMovie={handleSelectedMovie}
          />
        );
      })}
    </ul>
  );
}

function Movie({ movie, handleSelectedMovie }) {
  return (
    <li className="movie" onClick={() => handleSelectedMovie(movie.imdbID)}>
      <img src={movie.Poster} alt={movie.Title} />
      <div className="movieDetails">
        <p>{movie.Title}</p>
        <span>üóìÔ∏è {movie.Year}</span>
      </div>
    </li>
  );
}

function MovieDetails({ movie }) {
  return (
    <div className="movie-details-container">
      <div className="movie-details-header">
        <img src={movie.Poster} alt={movie.Title} />
        <div className="movie-details-title">
          <h3>{movie.Title}</h3>
          <p>{movie.Year} - Movie minutes</p>
        </div>
      </div>
      <div className="movie-rating-box">rating lists</div>
      <div className="movie-details-desc">
        <p>About the movie</p>
        <p>Staring: actors Names</p>
        <p>Director: Director Name</p>
      </div>
    </div>
  );
}

export default MainApp;
